// Ordenar por ano e garantir num√©rico
dados_ordenados = dic_raca_cor_ano
  .map(d =&gt; ({ ...d, ano: +d.ano }))
  .filter(d =&gt; !Number.isNaN(d.ano))
  .sort((a, b) =&gt; d3.ascending(a.ano, b.ano))

chart_ano = {
  const chartDom = DOM.element('div', { 
    style: `
      width: 100%; 
      height: 60vw; 
      max-height: 400px; 
      min-height: 300px;
    ` 
  });

  // Aguarda o DOM estar renderizado usando requestAnimationFrame
  requestAnimationFrame(() =&gt; {
    // Inicializa o gr√°fico ap√≥s o DOM estar pronto
    const myChart = echarts.init(chartDom);

    // Eixo X com anos (ordenados)
    const anos = dados_ordenados.map(d =&gt; d.ano)

    // Descobrir as s√©ries (todas as chaves exceto "ano")
    const racas = Object.keys(dados_ordenados[0] || {}).filter(k =&gt; k !== 'ano')

    // Ra√ßas que receber√£o markPoint no pico
    const racasParaMarcar = ['Branca', 'Preta', 'Parda']

    const option = {
      title: { text: 'Distribui√ß√£o por Ra√ßa/Cor ao longo dos anos' },
      tooltip: { trigger: 'axis' },
      legend: { data: racas, bottom: 10 },
      grid: { left: '3%', right: '4%', bottom: '15%', containLabel: true },
      toolbox: { feature: { saveAsImage: {} } },
      xAxis: {
        type: 'category',
        boundaryGap: false,
        data: anos,
        name: 'Ano'
      },
      yAxis: { type: 'value', name: 'Quantidade' },
      // paleta base (sobrescrevemos por s√©rie logo abaixo)
      color: ['#1f77b4', '#ff7f0e', '#2ca02c', '#d62728', '#9467bd'],
      animationDuration: 3000,
      animationEasing: 'cubicOut',
      series: racas.map((raca, index) =&gt; {
        const dados = dados_ordenados.map(d =&gt; +d[raca] || 0)
        const coresPorRaca = {
          Branca:   '#2E86AB',
          Parda:    '#A23B72',
          Preta:    '#F18F01',
          Amarela:  '#C73E1D',
          Ind√≠gena: '#592E83'
        }
        const cor = coresPorRaca[raca] || `hsl(${index * 72}, 70%, 50%)`
        return {
          name: raca,
          type: 'line',
          data: dados,
          itemStyle: { color: cor },
          lineStyle: { color: cor }
        }
      })
    }

    // Render inicial sem markPoints
    myChart.setOption(option)

    // Ap√≥s a anima√ß√£o, adicionar markPoints nos picos das s√©ries escolhidas
    setTimeout(() =&gt; {
      const seriesWithMarkPoints = racas.map((raca, index) =&gt; {
        const dados = dados_ordenados.map(d =&gt; +d[raca] || 0)
        const coresPorRaca = {
          Branca:   '#FED789',  // mantive sua paleta custom
          Parda:    '#72874E',
          Preta:    '#476F84',
          Amarela:  '#A4BED5',
          Ind√≠gena: '#453947'
        }
        const cor = coresPorRaca[raca] || `hsl(${index * 72}, 70%, 50%)`
        let markPoint = {}
        if (racasParaMarcar.includes(raca) && dados.length) {
          const valorMaximo = Math.max(...dados)
          const indiceMaximo = dados.indexOf(valorMaximo)
          const anoPico = anos[indiceMaximo]
          markPoint = {
            data: [{
              type: 'max',
              name: 'Pico',
              label: {
                show: false,
                position: 'top',
                formatter: `${raca} - pico: ${anoPico}`,
                backgroundColor: 'rgba(255, 255, 255, 0.9)',
                borderColor: cor,
                borderWidth: 1,
                borderRadius: 4,
                padding: [6, 10],
                fontSize: 12,
                fontWeight: 'bold'
              },
              symbol: 'circle',
              symbolSize: 8,
              itemStyle: { color: cor }
            }],
            animation: true,
            animationDuration: 800
          }
        }
        return {
          name: raca,
          type: 'line',
          data: dados,
          itemStyle: { color: cor },
          lineStyle: { color: cor },
          markPoint
        }
      })

      myChart.setOption({ series: seriesWithMarkPoints })
    }, 3100)

    // Event listener para redimensionamento
    const resizeHandler = () =&gt; {
      myChart.resize();
    };

    window.addEventListener('resize', resizeHandler);

    // Cleanup quando a c√©lula for re-executada
    invalidation.then(() =&gt; {
      window.removeEventListener('resize', resizeHandler);
      myChart.dispose();
    });
  });

  return chartDom;
}

























